"use strict";var Sidebar_types=require("./Sidebar.types.cjs");const sidebarReducer=(e,s)=>{switch(s.type){case Sidebar_types.sidebarActionEnum.hover:return Object.assign(Object.assign({},e),{hovered:s.payload.hovered,mobile:!1});case Sidebar_types.sidebarActionEnum.toggle:return Object.assign(Object.assign({},e),{expanded:!e.expanded,hovered:!1,mobile:!1});case Sidebar_types.sidebarActionEnum.expand:return Object.assign(Object.assign({},e),{expanded:s.payload.expanded,mobile:!1});case Sidebar_types.sidebarActionEnum.mobile:return Object.assign(Object.assign({},e),{expanded:!0,hovered:!1,mobile:!e.mobile})}return e};exports.sidebarReducer=sidebarReducer;
