"use strict";var array=require("./array.cjs");class SubscriptionManager{constructor(){this.subscriptions=[]}add(s){return array.addUniqueItem(this.subscriptions,s),()=>array.removeItem(this.subscriptions,s)}notify(s,i,r){const t=this.subscriptions.length;if(t)if(1===t)this.subscriptions[0](s,i,r);else for(let n=0;n<t;n++){const t=this.subscriptions[n];t&&t(s,i,r)}}getSize(){return this.subscriptions.length}clear(){this.subscriptions.length=0}}exports.SubscriptionManager=SubscriptionManager;
